import { Component, OnInit, Input, OnChanges, SimpleChanges, ViewChild, ElementRef, Output, EventEmitter } from '@angular/core';

interface ImageModel {
  type: string;
  image: string;
}

@Component({
  selector: 'ngx-horizontal-scroll-menu',
  standalone: false,
  template: `
    <nav class="d-flex" *ngIf="items?.length" [ngClass]="background">
      <div *ngIf="!hideNav" class="control d-flex align-items-center justify-content-center mousePointer" (mousedown)="left()"
      (click)="scrollLeft()" (mouseup)="clear()">
        <div class="prev" [ngClass]="{'opacity': !leftArrowHide}">
          <img [src]="leftIcon" width="24" />
        </div>
      </div>
      <div [ngClass]="!hideNav ? 'items-wrapper' : 'w-100'">
        <ul id="list-items" #hScroll ngxScrollMenu (scrolled)="listenToItemsScroll($event)">
          <li *ngFor="let item of items" class="px-2">
            <a *ngIf="item[linkLabel]" [id]="item?.title" (click)="onLinkClicked(item)"
            [ngClass]="text" [href]="item[linkLabel]">{{ item?.title }}</a>
            <a *ngIf="!item[linkLabel]" [id]="item?.title" (click)="onLinkClicked(item)" [ngClass]="text">{{ item?.title }}</a>
          </li>
        </ul>
      </div>

      <div *ngIf="!hideNav" class="control d-flex justify-content-center align-items-center mousePointer"
      (click)="scrollRight()" (mousedown)="right()" (mouseup)="clear()">
        <div class="next rotate-right" [ngClass]="{'opacity': !rightArrow}">
          <img [src]="leftIcon" width="24" />
        </div>
      </div>
    </nav>
  `,
  styles: [
    `
* {
    margin: 0px;
    padding: 0px;
    box-sizing: border-box;
}
    .d-flex {
      display: flex;
  }
  .justify-content-center {
      justify-content: center;
  }
  .align-items-center {
      align-items: center;
  }
  #list-items {
      padding: 0;
      display: flex;
      overflow: auto;
  }

  .w-100 {
    width: 100%;
  }

  #list-items li {
    list-style: none;
    padding: 10px;
  }

  #list-items a {
    text-decoration: none;
    cursor: pointer;
  }

  .rotate-right {
      transform: rotate(180deg);
  }
  .control {
      width: 50px;
  }

  .items-wrapper {
    width: calc(100% - 100px);
  }
  .prev, .next {
      width: 24px;
      opacity: 0;
  }
  .opacity {
      opacity: 1;
  }

  #list-items::-webkit-scrollbar {
    display: none;
  }

  #list-items {
    -ms-overflow-style: none;
}`
  ]
})
export class HorizontalScrollMenuComponent implements OnInit, OnChanges {

  @Input() linkLabel = 'link';
  @Input() items: any[] = this.generateItems();
  @Input() background: string;
  @Input() text: string;
  @Input() hideNav = false;
  @Input() navIcon: ImageModel;
  @Input() distance = 50;
  @Input() scrollSpeed = 100;

  @Output() clickedEventEmiiter = new EventEmitter<any>();

  leftArrowHide = true;
  rightArrow = false;

  // tslint:disable-next-line:max-line-length
  @Input() leftIcon = `data:image/png;base64,`;

  @ViewChild('hScroll', { static: false }) scrollWrapper: ElementRef;

  interval: any;

  constructor() {
  }

  ngOnInit(): void {
    setTimeout(() => {
      if (this.scrollWrapper.nativeElement.scrollWidth <= this.scrollWrapper.nativeElement.clientWidth) {
        this.leftArrowHide = true;
        this.rightArrow = true;
      }
    }, 100);

  }

  listenToItemsScroll(e): void {
    this.leftArrowHide = e.left_arrow;
    this.rightArrow = e.right_arrow;
  }

  scrollLeft(): void {
    const leftArrow = document.getElementById('list-items');
    const scrollLeft = leftArrow.scrollLeft;
    const distance = scrollLeft - this.distance;
    this.scroll(distance);

  }

  left(): void {
    const d = this;
    this.interval = setInterval(() => { d.scrollLeft(); }, this.scrollSpeed);
  }

  scrollRight(): void {
    const listWrapper = document.getElementById('list-items');
    const scrollLeft = listWrapper.scrollLeft;
    const distance = scrollLeft + this.distance;
    this.scroll(distance);
  }

  right(): void {
    const d = this;
    this.interval = setInterval(() => { d.scrollRight(); }, this.scrollSpeed);
  }

  scroll(distance): void {
    const listWrapper = document.getElementById('list-items');
    listWrapper.scrollTo({ behavior: 'smooth', left: distance });
  }

  clear(): void {
    clearInterval(this.interval);
  }

  generateItems(): any[] {
    const items = [];
    for (let i = 1; i < 50; i++) {
      items.push({ title: `Item${i}`, link: 'https://github.com/isahohieku' });
    }

    return items;
  }

  onLinkClicked(item: any): void {
    this.clickedEventEmiiter.emit(item);
  }

  ngOnChanges(simp: SimpleChanges): void {
    Object.keys(simp).forEach(key => this[key] = simp[key].currentValue);
  }

}
